use alloy_primitives::hex;
use cometbft_proto::types::v1::LightBlock as TmLightBlock;
use prost::Message;
// it might be useless and can be deleted later
#[test]
fn decode_light_block_tendermint_schema() {
    let block_bytes = hex!("");
    let res = TmLightBlock::decode(&block_bytes[..]);
    assert!(res.is_ok(), "decode should succeed with new schema but failed: {:?}", res.err());
} 